sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

	if ( newValue == '') {
			return;
		}
	var whatneeded = g_form.getValue('u_what_needed');
	 g_form.clearOptions('u_what_needed');


   if (newValue==='hr' ) {

	   g_form.addOption('u_what_needed','1','hr1');
	    g_form.addOption('u_what_needed','2','hr2');
	    g_form.addOption('u_what_needed','3','hr3');

   }
	 if (newValue === 'facilities' ) {

	   g_form.addOption('u_what_needed','1','facilities1');
	    g_form.addOption('u_what_needed','2','facilities2');
	    g_form.addOption('u_what_needed','3','facilities3');

   }

	if (newValue === 'legal' ) {

	   g_form.addOption('u_what_needed','1','legal1');
	    g_form.addOption('u_what_needed','2','legal2');
	    g_form.addOption('u_what_needed','3','legal3');

   }

	if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', whatneeded);
		}
   //Type appropriate comment here, and begin script below

}
